import styled from 'styled-components/macro';

const StyledArticle = styled.article`
  display: block;
  margin: 0;
  with: 100%;
  height: auto;
  z-index: 2;
  overflow: hidden;
  box-shadow: 0 3px 6px rgba(0, 0, 0, 0.5);
  border-radius: 0;
`;

const StyleCardContent = styled.section`
  position: absolute;
  buttom: 0px;
  left: 0;
  right: 0;
  padding: 1rem;
`;

const StyledCardH2 = styled.h2`
  
font-size: 1.5 rem;
  font-weight: 900;
`;

const StyledCardImage = styled.img`
  max-width: 90px;
`;

const StyledCardLink = styled.a`
  display: inline-block;
  text-aline: center;
  padding: 0.6rem 0.9rem;
  color: #fff;
  border: 1px solid #fff;
  font-style: italic;
`;

const StyledCardText = styled.p`
  font-size: 1.2rem;
  line-height: 2.4rem;
`;

const Card = ({ img, title, text, link }) => (
  <StyledArticle>
    <img src="" alt="" /> 
    <StyleCardContent>
      <h2>Title</h2>
      <p>Lead</p> 
     
      <a href="">Link</a>
    </StyleCardContent>
  </StyledArticle>
);
export default Card;
